var http=require('http');

var url= process.argv[2];

var str="";

http.get(url, function callback(response){
    response.setEncoding("utf8");
    response.on("data",function(data){
        str=str+data;        
    })
    response.on("end",function(){
        //console.log(str);
        console.log(str.length);
        console.log(str);
        
    })
});
