var http=require('http');

var a1="";
var a2="";
var a3="";

http.get(process.argv[2], function callback(response){
    response.setEncoding("utf8");
    response.on("data",function(data){
        a1=a1+data;        
    })
    response.on("end",function(){
        console.log(a1);
    })
});

